import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { getBusiness, generateAds } from '../../services/api';
import Loading from '../common/Loading';
import './GenerateAds.css';

const GenerateAds = () => {
  const [business, setBusiness] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [generating, setGenerating] = useState(false);
  const [adOptions, setAdOptions] = useState([]);
  const [adType, setAdType] = useState('text'); // 'text', 'image', or 'video'
  const [description, setDescription] = useState('');

  const navigate = useNavigate();

  useEffect(() => {
    const fetchBusiness = async () => {
      try {
        setLoading(true);
        const data = await getBusiness();

        if (data) {
          setBusiness(data);
          // Pre-populate description with business info
          setDescription(`${data.name} is a ${data.category} business. ${data.description}`);
        } else {
          setError('No business profile found');
          navigate('/business-setup');
        }
      } catch (error) {
        console.error('Error fetching business:', error);
        setError('Failed to load business data');
      } finally {
        setLoading(false);
      }
    };

    fetchBusiness();
  }, [navigate]);

  const handleGenerateAds = async () => {
    try {
      setGenerating(true);
      setError('');
      setAdOptions([]);

      const result = await generateAds(description);

      if (result && result.options) {
        setAdOptions(result.options);
      } else {
        setError('Failed to generate ad options');
      }
    } catch (error) {
      console.error('Error generating ads:', error);
      setError('Failed to generate ads: ' + error.message);
    } finally {
      setGenerating(false);
    }
  };

  if (loading) {
    return <Loading />;
  }

  return (
    <div className="container">
      <div className="service-header">
        <h1>Advertisement Generator</h1>
        <p>Create compelling advertisements for your business</p>
      </div>

      {error && <div className="alert alert-danger">{error}</div>}

      <div className="card mb-4">
        <div className="card-body">
          <h3 className="card-title mb-4">Generate Advertisements</h3>

          <div className="form-group mb-3">
            <label htmlFor="adType" className="form-label">Advertisement Type</label>
            <div className="d-flex gap-3">
              <div className="form-check">
                <input 
                  className="form-check-input" 
                  type="radio" 
                  name="adType" 
                  id="textAd" 
                  value="text" 
                  checked={adType === 'text'} 
                  onChange={() => setAdType('text')}
                />
                <label className="form-check-label" htmlFor="textAd">
                  Text Ad
                </label>
              </div>
              <div className="form-check">
                <input 
                  className="form-check-input" 
                  type="radio" 
                  name="adType" 
                  id="imageAd" 
                  value="image" 
                  checked={adType === 'image'} 
                  onChange={() => setAdType('image')}
                />
                <label className="form-check-label" htmlFor="imageAd">
                  Image Ad
                </label>
              </div>
              <div className="form-check">
                <input 
                  className="form-check-input" 
                  type="radio" 
                  name="adType" 
                  id="videoAd" 
                  value="video" 
                  checked={adType === 'video'} 
                  onChange={() => setAdType('video')}
                />
                <label className="form-check-label" htmlFor="videoAd">
                  Video Ad
                </label>
              </div>
            </div>
          </div>

          <div className="form-group mb-4">
            <label htmlFor="description" className="form-label">Business Description</label>
            <textarea 
              id="description" 
              className="form-control" 
              rows="5" 
              value={description} 
              onChange={(e) => setDescription(e.target.value)}
              placeholder="Describe your business, product, or service in detail..."
            ></textarea>
            <small className="text-muted">
              Include key details about your business, target audience, unique selling points, and what you want to promote.
            </small>
          </div>

          <div className="d-grid">
            <button 
              className="btn btn-primary" 
              onClick={handleGenerateAds}
              disabled={generating || !description.trim()}
            >
              {generating ? (
                <>
                  <span className="spinner-border spinner-border-sm me-2" role="status" aria-hidden="true"></span>
                  Generating...
                </>
              ) : (
                'Generate Advertisements'
              )}
            </button>
          </div>
        </div>
      </div>

      {adOptions.length > 0 && (
        <div className="card mb-4 ad-options-container">
          <div className="card-body">
            <h3 className="card-title mb-4">Advertisement Options</h3>

            <div className="ad-options">
              {adOptions.map((option, index) => (
                <div key={index} className="ad-option">
                  <h4>Option {index + 1}</h4>
                  <div className="ad-content">
                    {adType === 'text' ? (
                      <div className="text-ad">
                        <p>{option}</p>
                      </div>
                    ) : adType === 'image' ? (
                      <div className="image-ad">
                        <p>{option}</p>
                        <div className="image-placeholder">
                          <p>Image Ad Concept</p>
                          <small>Visual representation would be created based on this concept</small>
                        </div>
                      </div>
                    ) : (
                      <div className="video-ad">
                        <p>{option}</p>
                        <div className="video-placeholder">
                          <p>Video Ad Concept</p>
                          <small>Video storyboard would be created based on this concept</small>
                        </div>
                      </div>
                    )}
                  </div>
                  <div className="ad-actions">
                    <button className="btn btn-outline-primary btn-sm">
                      <i className="fas fa-download me-1"></i> Save
                    </button>
                    <button className="btn btn-outline-secondary btn-sm">
                      <i className="fas fa-edit me-1"></i> Edit
                    </button>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default GenerateAds;
