import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { getBusiness } from '../../services/api';
import Loading from '../common/Loading';
import './GenerateWebsite.css';

const GenerateWebsite = () => {
  const [business, setBusiness] = useState(null);
  const [loading, setLoading] = useState(true);
  const [generating, setGenerating] = useState(false);
  const [error, setError] = useState('');
  const [generatedHTML, setGeneratedHTML] = useState('');
  const [generatedCSS, setGeneratedCSS] = useState('');
  const [generatedJS, setGeneratedJS] = useState('');
  const [previewMode, setPreviewMode] = useState(false);
  const [activeTab, setActiveTab] = useState('html');

  const navigate = useNavigate();

  useEffect(() => {
    const fetchBusiness = async () => {
      try {
        setLoading(true);
        const data = await getBusiness();

        if (data) {
          setBusiness(data);
          console.log('Business data loaded:', data);
        } else {
          console.log('No business found for this user');
          setError('No business profile found');
          navigate('/business-setup');
        }
      } catch (error) {
        console.error('Error fetching business:', error);
        setError('Failed to load business data');
      } finally {
        setLoading(false);
      }
    };

    fetchBusiness();
  }, [navigate]);

  const generateWebsite = async () => {
    if (!business) return;

    try {
      setGenerating(true);
      setError('');

      console.log('Sending business data to generate website:', business.name);

      // Call the backend API to generate website
      const response = await fetch('http://localhost:5001/api/website/generate', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'x-auth-token': localStorage.getItem('token')
        },
        body: JSON.stringify({ business })
      });

      // Check if the response is JSON
      const contentType = response.headers.get('content-type');
      if (!contentType || !contentType.includes('application/json')) {
        // Handle non-JSON response
        const text = await response.text();
        console.error('Server returned non-JSON response:', text);
        throw new Error('Server returned an invalid response. Please try again later.');
      }

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.message || data.error || 'Failed to generate website');
      }

      if (!data.success) {
        throw new Error(data.message || 'Failed to generate website');
      }

      console.log('Website generated successfully');

      // Set the generated code
      setGeneratedHTML(data.html || '');
      setGeneratedCSS(data.css || '');
      setGeneratedJS(data.js || '');

      // Automatically show preview
      setPreviewMode(true);

    } catch (error) {
      console.error('Error generating website:', error);
      setError('Failed to generate website: ' + error.message);
    } finally {
      setGenerating(false);
    }
  };

  const downloadFiles = () => {
    if (!generatedHTML && !generatedCSS && !generatedJS) {
      alert('Please generate the website first');
      return;
    }

    // Create a zip-like structure using Blob
    const files = [
      { name: 'index.html', content: generatedHTML, type: 'text/html' },
      { name: 'styles.css', content: generatedCSS, type: 'text/css' },
      { name: 'script.js', content: generatedJS, type: 'text/javascript' }
    ];

    // Download each file individually
    files.forEach(file => {
      const blob = new Blob([file.content], { type: file.type });
      const url = URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = file.name;
      document.body.appendChild(a);
      a.click();
      document.body.removeChild(a);
      URL.revokeObjectURL(url);
    });
  };

  const togglePreview = () => {
    setPreviewMode(!previewMode);
  };

  if (loading) {
    return <Loading />;
  }

  // Create a data URL for the preview iframe
  const getPreviewUrl = () => {
    const html = `
      <!DOCTYPE html>
      <html>
        <head>
          <meta charset="UTF-8">
          <meta name="viewport" content="width=device-width, initial-scale=1.0">
          <title>${business?.name || 'Business Website'}</title>
          <style>${generatedCSS}</style>
        </head>
        <body>
          ${generatedHTML}
          <script>${generatedJS}</script>
        </body>
      </html>
    `;

    return URL.createObjectURL(new Blob([html], { type: 'text/html' }));
  };

  return (
    <div className="container">
      <div className="service-header">
        <h1>Website Generator</h1>
        <p>Generate a professional website for your business with AI</p>
      </div>

      {error && <div className="error-message">{error}</div>}

      <div className="card">
        <div className="business-summary">
          <h3>Generating Website For: {business?.name}</h3>
          <p><strong>Category:</strong> {business?.category}</p>
          <p><strong>Description:</strong> {business?.description}</p>
          <p><strong>Location:</strong> {business?.location}</p>
          <p><strong>Contact:</strong> {business?.contactNumber}</p>

          {business?.website && (
            <p><strong>Website:</strong> {business.website}</p>
          )}

          {business?.targetAudience && (
            <p><strong>Target Audience:</strong> {business.targetAudience}</p>
          )}

          {business?.yearsInBusiness && (
            <p><strong>Years in Business:</strong> {business.yearsInBusiness}</p>
          )}

          {business?.employeeCount && (
            <p><strong>Employees:</strong> {business.employeeCount}</p>
          )}

          {business?.enhancedProfile && (
            <div className="enhanced-profile-summary">
              <p><strong>AI-Enhanced Profile:</strong> Included</p>
            </div>
          )}
        </div>

        <div className="action-buttons">
          <button 
            className="btn btn-primary" 
            onClick={generateWebsite} 
            disabled={generating}
          >
            {generating ? 'Generating...' : 'Generate Website'}
          </button>

          {(generatedHTML || generatedCSS || generatedJS) && (
            <>
              <button className="btn btn-secondary" onClick={downloadFiles}>
                Download Files
              </button>
              <button className="btn btn-info" onClick={togglePreview}>
                {previewMode ? 'Hide Preview' : 'Show Preview'}
              </button>
            </>
          )}
        </div>
      </div>

      {(generatedHTML || generatedCSS || generatedJS) && (
        <div className="code-container">
          <div className="code-tabs">
            <button 
              className={`tab-button ${activeTab === 'html' ? 'active' : ''}`}
              onClick={() => setActiveTab('html')}
            >
              HTML
            </button>
            <button 
              className={`tab-button ${activeTab === 'css' ? 'active' : ''}`}
              onClick={() => setActiveTab('css')}
            >
              CSS
            </button>
            <button 
              className={`tab-button ${activeTab === 'js' ? 'active' : ''}`}
              onClick={() => setActiveTab('js')}
            >
              JavaScript
            </button>
          </div>

          <div className="code-editor">
            {activeTab === 'html' && (
              <pre className="code-content">{generatedHTML}</pre>
            )}
            {activeTab === 'css' && (
              <pre className="code-content">{generatedCSS}</pre>
            )}
            {activeTab === 'js' && (
              <pre className="code-content">{generatedJS}</pre>
            )}
          </div>
        </div>
      )}

      {previewMode && generatedHTML && (
        <div className="preview-container">
          <h3>Website Preview</h3>
          <iframe 
            src={getPreviewUrl()} 
            title="Website Preview" 
            className="preview-iframe"
          />
        </div>
      )}
    </div>
  );
};

export default GenerateWebsite;
