import React, { useState } from 'react';
import '../../styles/AdGeneration.css';

const GenerateAd = () => {
  // Only keep image and video types, remove text
  const [adType, setAdType] = useState('image');
  const [description, setDescription] = useState('');
  const [advertisementGoals, setAdvertisementGoals] = useState('');
  const [targetAudience, setTargetAudience] = useState('');
  const [adOptions, setAdOptions] = useState([]);
  const [loading, setLoading] = useState(false);

  const handleGenerateAd = async () => {
    setLoading(true);
    try {
      // Use fetch directly instead of imported function
      const response = await fetch('http://localhost:5005/generate-ads', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          ad_format: adType,
          description,
          advertisement_goals: advertisementGoals,
          target_audience: targetAudience
        }),
      });

      const data = await response.json();
      setAdOptions(data.options || []);
    } catch (error) {
      console.error('Error generating ad:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container">
      <h1 className="title">AI Advertisement Generator</h1>

      <div className="form-container">
        <h2>Generate Advertisement</h2>

        <div className="button-group">
          <button 
            className={`ad-type-button ${adType === 'image' ? 'active' : ''}`}
            onClick={() => setAdType('image')}
          >
            Image Advertisement
          </button>
          <button 
            className={`ad-type-button ${adType === 'video' ? 'active' : ''}`}
            onClick={() => setAdType('video')}
          >
            Video Advertisement
          </button>
        </div>

        <div className="form-field">
          <label htmlFor="description">Business/Product Description</label>
          <textarea
            id="description"
            rows="3"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
          />
        </div>

        <div className="form-field">
          <label htmlFor="goals">Advertisement Goals</label>
          <textarea
            id="goals"
            rows="2"
            value={advertisementGoals}
            onChange={(e) => setAdvertisementGoals(e.target.value)}
          />
        </div>

        <div className="form-field">
          <label htmlFor="audience">Target Audience</label>
          <textarea
            id="audience"
            rows="2"
            value={targetAudience}
            onChange={(e) => setTargetAudience(e.target.value)}
          />
        </div>

        <button 
          className="generate-button"
          onClick={handleGenerateAd}
          disabled={loading || !description || !advertisementGoals || !targetAudience}
        >
          {loading ? 'Generating...' : 'Generate Advertisement'}
        </button>
      </div>

      {/* Display generated options with increased size */}
      {adOptions.length > 0 && (
        <div className="options-container">
          <h2>Generated Options</h2>

          <div className="options-grid">
            {adOptions.map((option, index) => (
              <div key={index} className="ad-option">
                <h3>Option {index + 1}</h3>
                <div className="option-content">{option}</div>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default GenerateAd;
